bool CinzaT(){

}
// GAP
void GAP(){
	read_line_sensor();
	while(estado == 3){

		motor[motorA] = -20;
		motor[motorB] = -20;
		read_line_sensor();
		displayBigTextLine(10, "%d", estado);
	}
}

// Corrigir
void corrigir(int limiar){
	int erro = read_line_sensor() - SET_POINT;
	while(erro > limiar){
		erro = PID(read_line_sensor(), OFFSET, KP, SET_POINT);
	}
}

// Virada Genérica por erro
void gTurn(bool direction){
	int erro = read_line_sensor() - SET_POINT;
	while((erro > TURN_ERRO_K) || (erro < -TURN_ERRO_K)){
		displayBigTextLine(10, "Erro: %d", erro);
		if(direction){
			motor[motorA] = -20;
			motor[motorB] = 20;
		} else {
			motor[motorA] = 20;
			motor[motorB] = -20;
		}
		erro = read_line_sensor() - SET_POINT;
	}

}

//Saída do Verde
void gExit(){
	int cor;

	do{
		cor = read_color_sensor();
		motor[motorA] = -20;
		motor[motorB] = -20;
	}while(cor != 0);
}
//Virada do Verde
void greenTurn(bool side){
	gExit();
	walk(TURN_SPEED_90, TURN_TIME_90);
	turn(50, side);
	walk(TURN_SPEED_90, TURN_TIME_90/2);
	corrigir(8);
}
//Saída de estado
int StdOut(int mult, int std){
	do{
		read_line_sensor();
		motor[motorA] = 20 * mult;
		motor[motorB] = 20 * mult;
		int cor = read_color_sensor();

		// Detectando cor
		if(cor == 1){
			greenTurn(false);
			gExit();
			return 0;
		}else if(cor == 2){
			greenTurn(true);
			gExit();
			return 0;
		}
	}while(estado != std);
	return 0;
}
//Tratamento 90°
int grade90(bool side){
	// 90°
	// Checando por encruzilhadas
	read_line_sensor();
	int actualstt = estado;
	do{
		read_line_sensor();
		motor[motorA] = -20;
		motor[motorB] = -20;
		int cor = read_color_sensor();
		// Detectando cor
		if(cor == 1){
			greenTurn(false);
			gExit();
			return 0;
		}else if(cor == 2){
			greenTurn(true);
			gExit();
			return 0;
		}
	}while(estado == actualstt);
	if(estado == 4){
		//Caso seja uma encruzilhada ou um falso alerta, ajusta até estar na linha perfeitamente
		int erro = read_line_sensor() - SET_POINT;
		if((erro > 20) || (erro < -20)){
				turn(30, !side);
		}
		return 0;

	}
	// Checando se é uma curva de 90° ou mais
	else if(estado == 3){
		//Vira 90° à esquerda
		gTurn(side);
		//Anda para frente
		//Abaixo ele volta até encontrar a virada, depois vai pra frente e sai dela
		read_line_sensor();
		int Stdat = estado;
		//StdOut(1, Stdat);
		Stdat = estado;
		StdOut(-1, Stdat);
		read_line_sensor();
		Stdat = estado;

		corrigir(8);
		return 0;
	}
	return 0;
}
/**
* EURÍSTICA
*/
int euState(int cor){
	if(estado == 1){
		if (resgate)
			return 0;
		displayBigTextLine(10, "90 ESQUERDA %d", estado);
		// 90° ESQUERDA
		// Checando por encruzilhadas
		//mandando false/true
		grade90(true);
	}
	else if(estado == 2){
		displayBigTextLine(10, "90 DIREITA %d", estado);
		// 90° DIREITA
		// Ler estado atual
		grade90(false);
	}
	else if(estado == 3){
		GAP();
	}
	return 0;
}
/**
* LINE FOLLOWING
*/
void lineFollowing(){
	while(1){
		if (resgate)
			break;
		if(getIRDistance(infraR)<1){
			greenTurn(false);
			walk(TURN_SPEED_90, TURN_TIME_90*4);
			greenTurn(true);
			walk(TURN_SPEED_90, TURN_TIME_90*12);
			greenTurn(true);
			read_line_sensor();
			while(estado == 3){

				motor[motorA] = -30;
				motor[motorB] = -30;
				read_line_sensor();
			}
			greenTurn(false);
			walk(-TURN_SPEED_90, TURN_TIME_90*4);
		}
		int sensor = read_line_sensor();
		int cor = read_color_sensor();

		// Detectando cor
		if(cor == 1){
			greenTurn(false);
			gExit();
		}else if(cor == 2){
			greenTurn(true);
			gExit();
		}

		if((sensor <= 127) && (sensor >= 0) && (estado == 4)){
			PID(sensor, OFFSET, KP, SET_POINT);
			displayBigTextLine(10, "%d", estado);
			continue;
		}
		else{
			euState(cor);
		}
	}
}

/**
* RESGASTE
*/
void resgateMode(){
	while(1){
			linha = read_line_sensor();
			//PID(linha, 0, IMAGE_KP, IMAGE_SETPOINT);
			if(linha == 0){
				motor[motorA] = 0;
				motor[motorB] = 0;
			}else{
				if ((linha <= 100 + 5) && (linha >= 100 - 5)){
					motor[motorA] = 0;
					motor[motorB] = 0;
					//walk(10, 1);
				}else if (linha > 100){
					motor[motorA] = 5;
					motor[motorB] = -5;
				}else{
					motor[motorA] = -5;
					motor[motorB] = 5;
				}
			}
			wait1Msec(300);
	}
}

/* ---------------------------------
||						  MAIN	    				||
--------------------------------- */
task main()
{

	calibrateThresh();
	while(1){
		if(!resgate){
			eraseDisplay();
			lineFollowing();
		}
		else
			resgateMode();
		}
}
